import React, { useState} from 'react';
import MessageView from 'components/MessageView';
import { Message } from 'Domain/Message';
import MessageCompose from 'components/MessageCompose';
import MessageList from 'components/MessageList';

function Welcome(props: any): JSX.Element {
    const defaultMessage: Message = {
        id: '1337',
        name: 'Mr president',
        content: 'Cocojambo',
        timestamp: 4711
    }

    const [value, setValue] = useState(defaultMessage);

    const handleSubmit = (submittedMessage: Message) => {setValue([...submittedMessage])};

    return (
        <>
            <MessageView message={defaultMessage} />
            <MessageCompose onSubmit={handleSubmit} />
            <MessageList messages={[value]} />
        </>
    )
}

export default Welcome;
